import { useState } from "react";

function ListGroup() {
  let patterns = ["tua madre", "tua zia", "tua cugina", "tua sorella"];
  //hook
  const [selectdIndex, setSelectdIndex] = useState(-1);
  

  const getMessage = () => {
    return patterns.length === 0 && <p>Nun ce sta</p>;
  };

  return (
    <>
      <h1>List Group</h1>
      {getMessage()}

      <ul className="list-group">
        {patterns.map((pattern, index) => (
          <li
            key={pattern /*pattern.id per api.*/}
            className={
              selectdIndex === index
                ? "list-group-item active"
                : "list-group-item"
            }
            onClick={() => selectdIndex === index}
          >
            {pattern}
          </li>
        ))}
      </ul>
    </>
  );
}

export default ListGroup;
