import { useState } from "react";

interface Props {
  children: string;
  onClick?: () => void;
}

const Strategie = ({ children, onClick }: Props) => {
  const [showList, setShowList] = useState(false);

  function handleButtonClick() {
    setShowList(!showList);
    if (onClick) {
      onClick();
    }
  }

  return (
    <>
      <button className="btn pill" onClick={handleButtonClick}>
        {children}
      </button>
      {showList && (
        <ul>
          <li>Minimizzazione</li>
          <li>Occultamento</li>
          <li>Aggregazione</li>
          <li>Separazione</li>
          <li>Dimostrazione</li>
          <li>Indormazione</li>
          <li>Rafforzamento</li>
          <li>Controllo</li>
        </ul>
      )}
    </>
  );
};

export default Strategie;
