import { useState } from "react";

interface Props {
  onClick?: () => void;
}

const Vulnerability = ({ onClick }: Props) => {
  const [showList, setShowList] = useState(false);
  const [showList1, setShowList1] = useState(false);
  const [showList2, setShowList2] = useState(false);

  function handleButtonClick() {
    setShowList(!showList);
    if (onClick) {
      onClick();
    }
  }
  function handleButtonClick1() {
    setShowList(!showList);
    if (onClick) {
      onClick();
    }
  }
  function handleButtonClick2() {
    setShowList2(!showList2);
    if (onClick) {
      onClick();
    }
  }
  const OWASP = [
    "A01",
    "A02",
    "A03",
    "A04",
    "A05",
    "A06",
    "A07",
    "A08",
    "A09",
    "A10",
  ];

  const vulnerabilities = [
    "CWE-20: Improper Input Validation",
    "CWE-22: Improper Limitation of a Pathname to a Restricted Directory ('Path Traversal')",
    "CWE-77: Improper Neutralization of Special Elements used in a Command ('Command Injection')",
    "CWE-78: Improper Neutralization of Special Elements used in an OS Command ('OS Command Injection')",
    "CWE-79: Improper Neutralization of Input During Web Page Generation ('Cross-site Scripting')",
    "CWE-89: Improper Neutralization of Special Elements used in an SQL Command ('SQL Injection')",
    "CWE-94: Improper Control of Generation of Code ('Code Injection')",
    "CWE-269: Improper Privilege Management",
    "CWE-276: Incorrect Default Permissions",
    "CWE-287: Improper Authentication",
    "CWE-306: Missing Authentication for Critical Function",
    "CWE-434: Unrestricted Upload of File with Dangerous Type",
    "CWE-502: Deserialization of Untrusted Data",
    "CWE-798: Use of Hard-coded Credentials",
    "CWE-862: Missing Authorization",
    "CWE-863: Incorrect Authorization",
    "CWE-918: Server-Side Request Forgery (SSRF)"
  ];
  
 const CWE = []
  return (
    <>
      <button className="btn btn-info" onClick={handleButtonClick}>
        Vulnerabilit√†
      </button>
      {showList && (
        <>
          <button className="btn btn-info" onClick={handleButtonClick1}>
            OWASP
          </button>
          {showList1 && (
            <ul>
              <li>A01</li>
              <li>A02</li>
              <li>A03</li>
              <li>A04</li>
              <li>A05</li>
              <li>A06</li>
              <li>A07</li>
              <li>A08</li>
              <li>A09</li>
              <li>A10</li>
            </ul>
          )}
          <button className="btn btn-info" onClick={handleButtonClick2}>
            CWE
          </button>
          {showList2 && {}}
        </>
      )}
    </>
  );
};

export default Vulnerability;
